import { useState, useEffect, useRef } from 'react';
import { useSocket } from './useSocket.js';

export const useSchools = () => {
  const [schools, setSchools] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { socket, isConnected } = useSocket();
  const hasFetched = useRef(false);

  const API_BASE_URL = 'http://localhost:3001/api';

  const fetchSchools = async () => {
    if (hasFetched.current) return;
    
    try {
      setLoading(true);
      hasFetched.current = true;
      
      console.log('ðŸ“¡ Fetching schools...');
      const response = await fetch(`${API_BASE_URL}/schools`);
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const result = await response.json();
      
      if (result.success) {
        console.log('âœ… Schools loaded:', result.data.length);
        setSchools(result.data);
      } else {
        throw new Error(result.message || 'Failed to fetch schools');
      }
    } catch (error) {
      console.error('Error fetching schools:', error);
      setError(error.message);
      hasFetched.current = false;
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (!hasFetched.current) {
      fetchSchools();
    }
  }, []);

  useEffect(() => {
    if (!socket || !isConnected) return;

    console.log('ðŸ”Œ Setting up school listeners...');

    const handleNewSchool = (newSchool) => {
      console.log('ðŸ« New school received:', newSchool);
      setSchools(prev => [...prev, newSchool]);
    };

    const handleUpdatedSchool = (updatedSchool) => {
      console.log('ðŸ« School updated:', updatedSchool);
      setSchools(prev => 
        prev.map(school => 
          school._id === updatedSchool._id ? updatedSchool : school
        )
      );
    };

    const handleDeletedSchool = (data) => {
      console.log('ðŸ« School deleted:', data.id);
      setSchools(prev => 
        prev.filter(school => school._id !== data.id)
      );
    };

    socket.on('school_created', handleNewSchool);
    socket.on('school_updated', handleUpdatedSchool);
    socket.on('school_deleted', handleDeletedSchool);

    return () => {
      if (socket) {
        socket.off('school_created', handleNewSchool);
        socket.off('school_updated', handleUpdatedSchool);
        socket.off('school_deleted', handleDeletedSchool);
      }
    };
  }, [socket, isConnected]);

  const refetch = () => {
    hasFetched.current = false;
    fetchSchools();
  };

  return {
    schools,
    loading,
    error,
    fetchSchools: refetch,
    isConnected
  };
};
