import { useRealtimeData } from './useRealtimeData.js';

export const useSchools = () => {
  const { data, loading, error, refetch, isConnected } = useRealtimeData('schools');
  
  return {
    schools: data,
    loading,
    error,
    fetchSchools: refetch,
    isConnected
  };
};
