import React, { useState, useEffect } from 'react';

const Modal = ({ isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: '',
    program: '',
    grade: ''
  });
  const [showSuccess, setShowSuccess] = useState(false);
  const [showError, setShowError] = useState(false);

  // All program options with their respective grade choices
  const programOptions = [
    {
      value: "Nursery",
      label: "Nursery",
      grades: []
    },
    {
      value: "Kindergarten",
      label: "Kindergarten", 
      grades: []
    },
    {
      value: "Elementary",
      label: "Elementary",
      grades: [
        { value: "Grade 1", label: "Grade 1" },
        { value: "Grade 2", label: "Grade 2" },
        { value: "Grade 3", label: "Grade 3" },
        { value: "Grade 4", label: "Grade 4" },
        { value: "Grade 5", label: "Grade 5" },
        { value: "Grade 6", label: "Grade 6" }
      ]
    },
    {
      value: "Junior Highschool",
      label: "Junior Highschool",
      grades: [
        { value: "Grade 7", label: "Grade 7" },
        { value: "Grade 8", label: "Grade 8" }
      ]
    },
    {
      value: "Summer Class",
      label: "Summer Class",
      grades: []
    },
    {
      value: "One On One Tutorial",
      label: "One On One Tutorial",
      grades: []
    }
  ];

  // Get current program details
  const currentProgram = programOptions.find(prog => prog.value === formData.program);

  useEffect(() => {
    if (isOpen) {
      // Reset form when modal opens
      setFormData({
        name: '',
        email: '',
        phone: '',
        message: '',
        program: '',
        grade: ''
      });
      setShowSuccess(false);
      setShowError(false);
    }
  }, [isOpen]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
      // Reset grade when program changes
      ...(name === 'program' && { grade: '' })
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validate program selection
    if (!formData.program) {
      alert('Please select a program.');
      return;
    }

    // Validate grade selection for programs that require it
    if (currentProgram?.grades.length > 0 && !formData.grade) {
      alert(`Please select a grade level for ${formData.program}.`);
      return;
    }

    // Prepare the data to send to database
    const submissionData = {
      name: formData.name,
      email: formData.email,
      phone: formData.phone,
      message: formData.message,
      program: formData.program,
      grade: formData.grade || 'N/A', // Send 'N/A' if no grade
      program_display: formData.grade 
        ? `${formData.program} - ${formData.grade}`
        : formData.program,
      timestamp: new Date().toISOString(),
      status: 'new'
    };

    try {
      const response = await fetch('http://localhost:3001/api/inquiries', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(submissionData)
      });

      if (response.ok) {
        setShowSuccess(true);
        setShowError(false);
        
        // Reset form after successful submission
        setFormData({
          name: '',
          email: '',
          phone: '',
          message: '',
          program: '',
          grade: ''
        });
        
        // Close modal after 2 seconds
        setTimeout(() => {
          setShowSuccess(false);
          onClose();
        }, 2000);
      } else {
        throw new Error('Failed to save inquiry');
      }
    } catch (error) {
      setShowError(true);
      setShowSuccess(false);
      console.error('Error submitting inquiry:', error);
    }
  };

  const handleClose = () => {
    setFormData({
      name: '',
      email: '',
      phone: '',
      message: '',
      program: '',
      grade: ''
    });
    setShowSuccess(false);
    setShowError(false);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="modal active" onClick={handleClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <span className="close" onClick={handleClose}>&times;</span>
        <div className="modal-icon">
          <i className="fas fa-graduation-cap"></i>
        </div>
         
        <h2 className="modal-title">Program Inquiry</h2>
        <p className="modal-subtitle">Tell us about your interest and we'll contact you shortly</p>
        
        <form id="inquiryForm" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Full Name: *</label>
            <div className="input-with-icon">
              <div className="input-icon"><i className="fas fa-user"></i></div>
              <input 
                type="text" 
                id="name" 
                name="name" 
                placeholder="Your full name" 
                value={formData.name}
                onChange={handleChange}
                required 
              />
            </div>
          </div>
          
          <div className="form-group">
            <label htmlFor="email">Email Address: *</label>
            <div className="input-with-icon">
              <div className="input-icon"><i className="fas fa-envelope"></i></div>
              <input 
                type="email" 
                id="email" 
                name="email" 
                placeholder="Your email address" 
                value={formData.email}
                onChange={handleChange}
                required 
              />
            </div>
          </div>
          
          <div className="form-group">
            <label htmlFor="phone">Phone Number:</label>
            <div className="input-with-icon">
              <div className="input-icon"><i className="fas fa-phone"></i></div>
              <input 
                type="tel" 
                id="phone" 
                name="phone" 
                placeholder="Your phone number" 
                value={formData.phone}
                onChange={handleChange}
              />
            </div>
          </div>
          
          {/* Program Selection - Required */}
          <div className="form-group">
            <label htmlFor="program">Program of Interest: *</label>
            <div className="input-with-icon">
              <div className="input-icon"><i className="fas fa-graduation-cap"></i></div>
              <select
                id="program"
                name="program"
                value={formData.program}
                onChange={handleChange}
                required
                className={!formData.program ? 'placeholder' : ''}
              >
                <option value="">Select a program...</option>
                {programOptions.map(program => (
                  <option key={program.value} value={program.value}>
                    {program.label}
                  </option>
                ))}
              </select>
            </div>
          </div>
          
          {/* Grade Selection (Conditional) */}
          {currentProgram && currentProgram.grades.length > 0 && (
            <div className="form-group">
              <label htmlFor="grade">Grade Level: *</label>
              <div className="input-with-icon">
                <div className="input-icon"><i className="fas fa-star"></i></div>
                <select
                  id="grade"
                  name="grade"
                  value={formData.grade}
                  onChange={handleChange}
                  required
                  className={!formData.grade ? 'placeholder' : ''}
                >
                  <option value="">Select a grade...</option>
                  {currentProgram.grades.map(grade => (
                    <option key={grade.value} value={grade.value}>
                      {grade.label}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          )}
          
          <div className="form-group">
            <label htmlFor="message">Additional Information: *</label>
            <div className="input-with-icon">
              <div className="input-icon" style={{alignItems: 'flex-start', paddingTop: '16px'}}>
                <i className="fas fa-comment"></i>
              </div>
              <textarea 
                id="message" 
                name="message" 
                rows="4" 
                placeholder="Tell us about your inquiry, specific interests, or any questions you have..."
                value={formData.message}
                onChange={handleChange}
                required
              ></textarea>
            </div>
          </div>
          
          <button type="submit" className="submit-btn">
            Submit Inquiry
          </button>
        </form>
        
        <div className={`success-message ${showSuccess ? 'active' : ''}`}>
          <i className="fas fa-check-circle"></i> 
          <div>
            <strong>Thank you for your inquiry!</strong>
            <p>We'll get back to you within 24 hours.</p>
          </div>
        </div>
        
        <div className={`error-message ${showError ? 'active' : ''}`}>
          <i className="fas fa-exclamation-circle"></i> 
          <div>
            <strong>Submission Error</strong>
            <p>There was an error submitting your inquiry. Please try again.</p>
          </div>
        </div>
        
        <div className="modal-footer">
          <p><i className="fas fa-shield-alt"></i> We value your privacy. Your information will not be shared with third parties.</p>
        </div>
      </div>
    </div>
  );
};

export default Modal;
