import React, { useState } from 'react';

const SchoolsGallery = () => {
  const [selectedImage, setSelectedImage] = useState(null);

  // Sample school images data - replace with your actual images
  const schoolImages = [
    {
      id: 1,
      src: "/image/announcement1.jpg",
      alt: "School Building Front View",
      name: "Greenwood High School",
      description: "Main campus building with modern architecture"
    },
    {
      id: 2,
      src: "/images/school2.jpg",
      alt: "School Library",
      name: "Knowledge Academy",
      description: "State-of-the-art library with digital resources"
    },
    {
      id: 3,
      src: "/images/school3.jpg",
      alt: "Science Laboratory",
      name: "Science Valley School",
      description: "Fully equipped science lab for experiments"
    },
    {
      id: 4,
      src: "/images/school4.jpg",
      alt: "Sports Ground",
      name: "Sports Academy",
      description: "Olympic-sized sports ground and track"
    },
    {
      id: 5,
      src: "/images/school5.jpg",
      alt: "Computer Lab",
      name: "Tech Institute",
      description: "Modern computer lab with latest technology"
    },
    {
      id: 6,
      src: "/images/school6.jpg",
      alt: "Art Classroom",
      name: "Creative Arts School",
      description: "Spacious art room for creative expression"
    }
  ];

  const openModal = (image) => {
    setSelectedImage(image);
  };

  const closeModal = () => {
    setSelectedImage(null);
  };

  return (
    <div className="schools-gallery">
      <div className="gallery-header">
        <h2>Our Schools Gallery</h2>
        <p>Explore our educational facilities and campuses</p>
      </div>

      <div className="gallery-grid">
        {schoolImages.map((image) => (
          <div 
            key={image.id} 
            className="gallery-item"
            onClick={() => openModal(image)}
          >
            <div className="image-container">
              <img 
                src={image.src} 
                alt={image.alt}
                onError={(e) => {
                  e.target.src = 'https://via.placeholder.com/300x200/4A90E2/FFFFFF?text=School+Image';
                }}
              />
              <div className="image-overlay">
                <h3>{image.name}</h3>
                <p>{image.description}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Modal for enlarged image view */}
      {selectedImage && (
        <div className="modal" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close" onClick={closeModal}>&times;</span>
            <img 
              src={selectedImage.src} 
              alt={selectedImage.alt}
              onError={(e) => {
                e.target.src = 'https://via.placeholder.com/600x400/4A90E2/FFFFFF?text=School+Image';
              }}
            />
            <div className="modal-info">
              <h3>{selectedImage.name}</h3>
              <p>{selectedImage.description}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SchoolsGallery;
