import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SchoolsGallery = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [schools, setSchools] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSchools = async () => {
      try {
        setLoading(true);
        setError(null);
        const response = await axios.get('http://localhost:3001/api/schools');
        
        if (response.data.success) {
          setSchools(response.data.data);
        } else {
          throw new Error(response.data.message || 'Failed to fetch schools');
        }
      } catch (error) {
        console.error('Error fetching schools:', error);
        setError('Failed to load schools gallery. Please try again later.');
        // Set empty array to prevent map errors
        setSchools([]);
      } finally {
        setLoading(false);
      }
    };

    fetchSchools();
  }, []);

  const openModal = (school) => {
    setSelectedImage(school);
  };

  const closeModal = () => {
    setSelectedImage(null);
  };

  // Handle click outside modal
  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      closeModal();
    }
  };

  // Handle escape key to close modal
  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape') {
        closeModal();
      }
    };

    if (selectedImage) {
      document.addEventListener('keydown', handleEscape);
      document.body.style.overflow = 'hidden'; // Prevent background scrolling
    }

    return () => {
      document.removeEventListener('keydown', handleEscape);
      document.body.style.overflow = 'unset';
    };
  }, [selectedImage]);

  if (loading) {
    return (
      <section id="gallery" className="section gallery">
        <div className="schools-gallery">
          <div className="gallery-header">
            <h2>Our Schools Gallery</h2>
            <p>Explore our educational facilities and campuses</p>
          </div>
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>Loading schools...</p>
          </div>
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section id="gallery" className="section gallery">
        <div className="schools-gallery">
          <div className="gallery-header">
            <h2>Our Schools Gallery</h2>
            <p>Explore our educational facilities and campuses</p>
          </div>
          <div className="error-container">
            <div className="error-icon">⚠️</div>
            <p>{error}</p>
            <button 
              onClick={() => window.location.reload()} 
              className="retry-button"
            >
              Try Again
            </button>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section id="gallery" className="section gallery">
      <div className="schools-gallery">
        <div className="gallery-header">
            
          <h2>Our Schools Gallery</h2>
          <p>Explore our educational facilities and campuses</p>
        </div>

        {schools.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">🏫</div>
            <h3>No Schools Available</h3>
            <p>There are no schools to display at the moment.</p>
          </div>
        ) : (
          <div className="gallery-grid">
            {schools.map((school) => (
              <div 
                key={school._id} 
                className="gallery-item"
                onClick={() => openModal(school)}
              >
                <div className="image-container">
                  <img 
                    src={school.imageUrl} 
                    alt={school.name}
                    loading="lazy" // Lazy load images
                    onError={(e) => {
                      e.target.src = 'https://via.placeholder.com/300x200/4A90E2/FFFFFF?text=School+Image';
                      e.target.alt = 'Default school image';
                    }}
                  />
                  <div className="image-overlay">
                    <h3>{school.name}</h3>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Modal for enlarged image view */}
        {selectedImage && (
          <div className="modal" onClick={handleBackdropClick}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <span className="close" onClick={closeModal}>&times;</span>
              <div className="modal-image-container">
                <img 
                  src={selectedImage.imageUrl} 
                  alt={selectedImage.name}
                  onError={(e) => {
                    e.target.src = 'https://via.placeholder.com/600x400/4A90E2/FFFFFF?text=School+Image';
                    e.target.alt = 'Default school image';
                  }}
                />
              </div>
              <div className="modal-info">
                <h3>{selectedImage.name}</h3>
              </div>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default SchoolsGallery;
