import { useState, useEffect } from 'react';
import { useSocket } from './useSocket';

export const useSchools = () => {
  const [schools, setSchools] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { socket } = useSocket();

  const API_BASE_URL = 'http://localhost:3001/api';

  const fetchSchools = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await fetch(`${API_BASE_URL}/schools`);
      const result = await response.json();
      
      if (result.success) {
        setSchools(result.data);
      } else {
        throw new Error(result.message || 'Failed to fetch schools');
      }
    } catch (error) {
      console.error('Error fetching schools:', error);
      setError('Failed to load schools gallery. Please try again later.');
      setSchools([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchSchools();

    // Set up real-time listeners
    if (socket) {
      const handleNewSchool = (newSchool) => {
        console.log('🏫 New school received:', newSchool);
        setSchools(prev => [...prev, newSchool]);
      };

      const handleUpdatedSchool = (updatedSchool) => {
        console.log('🏫 School updated:', updatedSchool);
        setSchools(prev => 
          prev.map(school => 
            school._id === updatedSchool._id ? updatedSchool : school
          )
        );
      };

      const handleDeletedSchool = (data) => {
        console.log('🏫 School deleted:', data.id);
        setSchools(prev => 
          prev.filter(school => school._id !== data.id)
        );
      };

      socket.on('school_created', handleNewSchool);
      socket.on('school_updated', handleUpdatedSchool);
      socket.on('school_deleted', handleDeletedSchool);

      return () => {
        socket.off('school_created', handleNewSchool);
        socket.off('school_updated', handleUpdatedSchool);
        socket.off('school_deleted', handleDeletedSchool);
      };
    }
  }, [socket]);

  return {
    schools,
    loading,
    error,
    fetchSchools
  };
};
