import React, { useState, useEffect } from 'react';
import { useAnnouncements } from '../hooks/useAnnouncements.js';

const Announcements = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const { announcements, loading, error, isConnected } = useAnnouncements();

  useEffect(() => {
    if (announcements.length > 0) {
      const interval = setInterval(() => {
        setCurrentSlide((prev) => (prev + 1) % announcements.length);
      }, 5000);

      return () => clearInterval(interval);
    }
  }, [announcements.length]);

  const showSlide = (index) => {
    setCurrentSlide(index);
  };

  if (loading) {
    return (
      <section id="announcements" className="section announcements">
        <div className="container">
          <h2 className="section-title">Recent <span>Announcements</span></h2>
          <div className="loading">
            <div className="loading-spinner"></div>
            Loading announcements...
            {!isConnected && <div className="connection-warning">(Connecting to real-time updates...)</div>}
          </div>
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section id="announcements" className="section announcements">
        <div className="container">
          <h2 className="section-title">Recent <span>Announcements</span></h2>
          <div className="error-message">
            {error}
            <button onClick={() => window.location.reload()} className="retry-button">
              Retry
            </button>
          </div>
        </div>
      </section>
    );
  }

  if (announcements.length === 0) {
    return (
      <section id="announcements" className="section announcements">
        <div className="container">
          <h2 className="section-title">Recent <span>Announcements</span></h2>
          <div className="no-announcements">
            No announcements available at the moment.
            {!isConnected && <div className="connection-warning">(Real-time updates disconnected)</div>}
          </div>
        </div>
      </section>
    );
  }

  return (
    <section id="announcements" className="section announcements">
      <div className="container">
        <h2 className="section-title">Recent <span>Announcements</span></h2>
        
        {!isConnected && (
          <div className="connection-banner">
            ⚠️ Disconnected - updates may not be real-time
          </div>
        )}
        
        <div className="announcement-slider">
          <div className="announcement-track">
            {announcements.map((announcement, index) => (
              <div 
                key={announcement._id} 
                className="announcement-slide" 
                style={{ display: index === currentSlide ? 'flex' : 'none' }}
              >
                <div className="announcement-info">
                  <h3 className="announcement-title">{announcement.title}</h3>
                  <p className="announcement-date">{announcement.date}</p>
                  <p className="announcement-desc">{announcement.description}</p>
                  {announcement.isNew && <span className="new-badge">NEW</span>}
                </div>
                <div className="announcement-img">
                  <img 
                    src={`http://localhost:3001/uploads/${announcement.image}`} 
                    alt={announcement.title}
                    onError={(e) => {
                      e.target.src = `${process.env.PUBLIC_URL}/image/placeholder.jpg`;
                    }}
                  />
                </div>
              </div>
            ))}
          </div>
          
          <div className="slider-nav">
            {announcements.map((_, index) => (
              <div 
                key={index}
                className={`slider-dot ${index === currentSlide ? 'active' : ''}`}
                onClick={() => showSlide(index)}
              ></div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Announcements;
