import React, { useState, useEffect } from 'react';

const Modal = ({ isOpen, onClose, selectedProgram }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: '',
    grade: ''
  });
  const [showSuccess, setShowSuccess] = useState(false);
  const [showError, setShowError] = useState(false);

  const hasGradeChoices = selectedProgram === "Elementary" || selectedProgram === "Junior Highschool";

  const gradeOptions = {
    "Elementary": [
      { value: "Grade 1", label: "Grade 1" },
      { value: "Grade 2", label: "Grade 2" },
      { value: "Grade 3", label: "Grade 3" },
      { value: "Grade 4", label: "Grade 4" },
      { value: "Grade 5", label: "Grade 5" },
      { value: "Grade 6", label: "Grade 6" }
    ],
    "Junior Highschool": [
      { value: "Grade 7", label: "Grade 7" },
      { value: "Grade 8", label: "Grade 8" },
      { value: "Grade 9", label: "Grade 9" },
      { value: "Grade 10", label: "Grade 10" }
    ]
  };

  const getGradeOptions = () => {
    return gradeOptions[selectedProgram] || [];
  };

  useEffect(() => {
    if (isOpen) {
      setFormData({
        name: '',
        email: '',
        phone: '',
        message: '',
        grade: ''
      });
      setShowSuccess(false);
      setShowError(false);
    }
  }, [isOpen, selectedProgram]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (hasGradeChoices && !formData.grade) {
      alert(`Please select a grade level for ${selectedProgram}.`);
      return;
    }

    const submissionData = {
      name: formData.name,
      email: formData.email,
      phone: formData.phone,
      message: formData.message,
      program: selectedProgram,
      grade: hasGradeChoices ? formData.grade : 'N/A',
      programDisplay: hasGradeChoices && formData.grade 
        ? `${selectedProgram} - ${formData.grade}`
        : selectedProgram,
      timestamp: new Date()
    };

    try {
      const response = await fetch('http://localhost:3001/api/inquiries', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(submissionData)
      });

      if (response.ok) {
        setShowSuccess(true);
        setShowError(false);
        setFormData({ 
          name: '', 
          email: '', 
          phone: '', 
          message: '',
          grade: ''
        });
        
        setTimeout(() => {
          setShowSuccess(false);
          onClose();
        }, 2000);
      } else {
        throw new Error('Failed to save inquiry');
      }
    } catch (error) {
      setShowError(true);
      setShowSuccess(false);
      console.error('Error:', error);
    }
  };

  const handleClose = () => {
    setFormData({ 
      name: '', 
      email: '', 
      phone: '', 
      message: '',
      grade: ''
    });
    setShowSuccess(false);
    setShowError(false);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="modal active" onClick={handleClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <span className="close" onClick={handleClose}>&times;</span>
        <div className="modal-icon">
          <i className="fas fa-envelope"></i>
        </div>
         
        <h2 className="modal-title">Inquiry Form - {selectedProgram}</h2>
        <p className="modal-subtitle">We'll get back to you within 24 hours</p>
        
        <form id="inquiryForm" onSubmit={handleSubmit}>
          {/* ... other form fields ... */}
          
          {/* Grade Selection with Enhanced Styling */}
          {hasGradeChoices && (
            <div className={`grade-selection ${selectedProgram.toLowerCase().replace(' ', '-')}`}>
              <label htmlFor="grade">
                <i className="fas fa-graduation-cap"></i> Grade Level: *
              </label>
              <div className="grade-select-container">
                <select
                  id="grade"
                  name="grade"
                  value={formData.grade}
                  onChange={handleChange}
                  required
                  className={`grade-select ${formData.grade ? 'has-selection' : ''}`}
                >
                  <option value="">Choose your grade level...</option>
                  {getGradeOptions().map(grade => (
                    <option key={grade.value} value={grade.value}>
                      {grade.label}
                    </option>
                  ))}
                </select>
                <div className="grade-select-icon">
                  <i className={
                    selectedProgram === "Elementary" ? "fas fa-child" : "fas fa-user-graduate"
                  }></i>
                </div>
              </div>
              <small className="grade-help">
                {selectedProgram === "Elementary" 
                  ? "Select from Grades 1 to 6 for Elementary program"
                  : "Select from Grades 7 to 10 for Junior Highschool program"
                }
              </small>
            </div>
          )}
          
          {/* ... other form fields ... */}
          
          <button type="submit" className="submit-btn">
            Submit Inquiry
          </button>
        </form>
        
        {/* ... success/error messages ... */}
      </div>
    </div>
  );
};

export default Modal;
