import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Reviews = () => {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newReview, setNewReview] = useState({
    name: '',
    rating: 5,
    comment: ''
  });

  const API_BASE_URL = 'http://localhost:3001/api';

  // Fetch reviews from server
  const fetchReviews = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_BASE_URL}/reviews`);
      if (response.data.success) {
        setReviews(response.data.data);
      } else {
        setError('Failed to fetch reviews');
      }
    } catch (err) {
      console.error('Error fetching reviews:', err);
      setError('Error loading reviews. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  // Submit new review to server
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setError('');
      const response = await axios.post(`${API_BASE_URL}/reviews`, newReview);
      
      if (response.data.success) {
        setNewReview({ name: '', rating: 5, comment: '' });
        setIsModalOpen(false);
        alert('Review submitted successfully!');
      } else {
        setError('Failed to submit review');
      }
    } catch (err) {
      console.error('Error submitting review:', err);
      setError('Error submitting review. Please try again.');
    }
  };

  const handleChange = (e) => {
    setNewReview({
      ...newReview,
      [e.target.name]: e.target.value
    });
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setNewReview({ name: '', rating: 5, comment: '' });
    setError('');
  };

  // Set up real-time updates using your existing WebSocket setup
  useEffect(() => {
    fetchReviews();

    // Connect to WebSocket for real-time updates
    const socket = new WebSocket('ws://localhost:3001');
    
    socket.onopen = () => {
      console.log('WebSocket connected for reviews');
    };

    socket.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        
        // Handle real-time review updates from your server
        if (data.type === 'review_created') {
          setReviews(prev => [data.payload, ...prev]);
        } else if (data.type === 'review_updated') {
          setReviews(prev => prev.map(review => 
            review._id === data.payload._id ? data.payload : review
          ));
        } else if (data.type === 'review_deleted') {
          setReviews(prev => prev.filter(review => review._id !== data.payload.id));
        }
      } catch (err) {
        console.error('Error parsing WebSocket message:', err);
      }
    };

    socket.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    return () => {
      socket.close();
    };
  }, []);

  if (loading && reviews.length === 0) {
    return <div className="reviews-container">Loading reviews...</div>;
  }

  return (
    <div className="reviews-container">
      <h2 className="section-title">School<span>Reviews</span></h2>
      
      {/* Add Review Button */}
      <div className="add-review-section">
        <button className="add-review-btn" onClick={openModal}>
          Write a Review
        </button>
      </div>

      {/* Modal */}
      {isModalOpen && (
        <div className="1modal-overlay" onClick={closeModal}>
          <div className="1modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="1modal-header">
              <h3>Add Your Review</h3>
              <button className="1close-btn" onClick={closeModal}>×</button>
            </div>
            
            {error && (
              <div className="1error-message" style={{color: 'red', marginBottom: '1rem'}}>
                {error}
              </div>
            )}
            
            <form onSubmit={handleSubmit} className="1review-form">
              <div className="1form-group">
                <label>Name:</label>
                <input
                  type="text"
                  name="name"
                  value={newReview.name}
                  onChange={handleChange}
                  required
                  placeholder="Your name"
                />
              </div>
              
              <div className="1form-group">
                <label>Rating:</label>
                <select
                  name="rating"
                  value={newReview.rating}
                  onChange={handleChange}
                >
                  <option value={5}>5 Stars</option>
                  <option value={4}>4 Stars</option>
                  <option value={3}>3 Stars</option>
                  <option value={2}>2 Stars</option>
                  <option value={1}>1 Star</option>
                </select>
              </div>
              
              <div className="1form-group">
                <label>Comment:</label>
                <textarea
                  name="comment"
                  value={newReview.comment}
                  onChange={handleChange}
                  required
                  rows={4}
                  placeholder="Share your experience..."
                />
              </div>
              
              <div className="1form-actions">
                <button type="submit" className="1submit-btn">
                  Submit Review
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Reviews List */}
      <div className="reviews-list">
        <h3>What People Are Saying ({reviews.length})</h3>
        {reviews.length === 0 ? (
          <p>No reviews yet. Be the first to share your experience!</p>
        ) : (
          reviews.map(review => (
            <div key={review._id} className="review-item">
              <div className="review-header">
                <div className="reviewer-name">{review.name}</div>
                <div className="review-date">{review.date}</div>
              </div>
              <div className="review-rating">
                {'★'.repeat(review.rating)}{'☆'.repeat(5 - review.rating)}
              </div>
              <div className="review-comment">{review.comment}</div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Reviews;
