import React from 'react';

const Hero = () => {
  return (
    <section 
      id="hero" 
      className="hero"
      style={{
        backgroundImage: `url('/image/announcement2.jpg')`, // Image in public/images folder
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        minHeight: '200vh',
        width: '200%'
      }}
    >
      <div className="container">
        <div className="hero-content">
          <h1 className="hero-title">Jem Brilliant Mind Montissori School Inc.</h1>
          <p className="hero-subtitle"> Provides quality education that nurtures young minds and prepares them for a bright future.</p>
          <a href="#programs" className="btn">Explore Our Programs</a>
        </div>
      </div>
    </section>
  );
};

export default Hero;
