import React from 'react';

const Hero = () => {
  return (
    <section id="hero" className="hero">
      {/* Animated background elements */}
      <div className="hero-bg-elements">
        <div className="floating-shape shape-1"></div>
        <div className="floating-shape shape-2"></div>
        <div className="floating-shape shape-3"></div>
        <div className="grid-lines"></div>
      </div>
      
      <div className="container">
        <div className="hero-content">
          {/* Main title with gradient effect */}
          <h1 className="hero-title">
            <span className="title-main">Jem Brilliant Mind</span>
            <span className="title-sub">Montissori School Inc.</span>
          </h1>
          
          {/* Subtitle with animated border */}
          <div className="subtitle-container">
            <p className="hero-subtitle">
              Provides quality education that nurtures young minds and prepares them for a bright future.
            </p>
          </div>
          
          {/* Futuristic button */}
          <a href="#programs" className="btn futuristic-btn">
            <span className="btn-text">Explore Our Programs</span>
            <div className="btn-glow"></div>
          </a>
        </div>
      </div>
    </section>
  );
};

export default Hero;
