import { useState, useEffect } from 'react';
import { useSocket } from './useSocket';

export const useAnnouncements = () => {
  const [announcements, setAnnouncements] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { socket } = useSocket();

  const API_BASE_URL = 'http://localhost:3001/api';

  const fetchAnnouncements = async () => {
    try {
      setLoading(true);
      const response = await fetch(`${API_BASE_URL}/announcements`);
      const result = await response.json();
      
      if (result.success) {
        setAnnouncements(result.data);
      } else {
        setError('Failed to load announcements');
      }
    } catch (error) {
      console.error('Error fetching announcements:', error);
      setError('Error loading announcements');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAnnouncements();

    // Set up real-time listeners
    if (socket) {
      socket.on('announcement_created', (newAnnouncement) => {
        console.log('游닉 New announcement received:', newAnnouncement);
        setAnnouncements(prev => [newAnnouncement, ...prev]);
      });

      socket.on('announcement_updated', (updatedAnnouncement) => {
        console.log('游닉 Announcement updated:', updatedAnnouncement);
        setAnnouncements(prev => 
          prev.map(ann => 
            ann._id === updatedAnnouncement._id ? updatedAnnouncement : ann
          )
        );
      });

      socket.on('announcement_deleted', (data) => {
        console.log('游닉 Announcement deleted:', data.id);
        setAnnouncements(prev => 
          prev.filter(ann => ann._id !== data.id)
        );
      });
    }

    return () => {
      if (socket) {
        socket.off('announcement_created');
        socket.off('announcement_updated');
        socket.off('announcement_deleted');
      }
    };
  }, [socket]);

  return {
    announcements,
    loading,
    error,
    fetchAnnouncements
  };
};
