import React from 'react';

const Hero = () => {
  const heroStyle = {
    backgroundImage: `url('/image/2.jpg')`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    backgroundAttachment: 'fixed',
    minHeight: '100vh',
    width: '100%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    position: 'relative'
  };

  const overlayStyle = {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.3)',
    zIndex: 1
  };

  const contentStyle = {
    position: 'relative',
    zIndex: 2,
    textAlign: 'center',
    color: 'white',
    width: '100%'
  };

  return (
    <section id="hero" style={heroStyle}>
      <div style={overlayStyle}></div>
      <div style={contentStyle}>
        <h1 style={{ fontSize: '3rem', marginBottom: '1rem', textShadow: '2px 2px 4px rgba(0,0,0,0.5)' }}>
          Jem Brilliant Mind Montissori School Inc.
        </h1>
        <p style={{ fontSize: '1.5rem', marginBottom: '2rem', textShadow: '1px 1px 2px rgba(0,0,0,0.5)' }}>
          Provides quality education that nurtures young minds and prepares them for a bright future.
        </p>
        <a href="#programs" style={{
          display: 'inline-block',
          padding: '12px 30px',
          backgroundColor: '#ff6b6b',
          color: 'white',
          textDecoration: 'none',
          borderRadius: '5px',
          fontWeight: 'bold',
          transition: 'background-color 0.3s ease'
        }}>
          Explore Our Programs
        </a>
      </div>
    </section>
  );
};

export default Hero;
