import { useState, useEffect } from 'react';
import { useSocket } from './useSocket';

export const useSchools = () => {
  const [schools, setSchools] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { socket } = useSocket();

  const API_BASE_URL = 'http://localhost:3001/api';

  const fetchSchools = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await fetch(`${API_BASE_URL}/schools`);
      const result = await response.json();
      
      if (result.success) {
        setSchools(result.data);
      } else {
        throw new Error(result.message || 'Failed to fetch schools');
      }
    } catch (error) {
      console.error('Error fetching schools:', error);
      setError('Failed to load schools gallery. Please try again later.');
      setSchools([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchSchools();

    // Set up real-time listeners
    if (socket) {
      socket.on('school_created', (newSchool) => {
        console.log('ðŸ« New school received:', newSchool);
        setSchools(prev => [...prev, newSchool]);
      });

      socket.on('school_updated', (updatedSchool) => {
        console.log('ðŸ« School updated:', updatedSchool);
        setSchools(prev => 
          prev.map(school => 
            school._id === updatedSchool._id ? updatedSchool : school
          )
        );
      });

      socket.on('school_deleted', (data) => {
        console.log('ðŸ« School deleted:', data.id);
        setSchools(prev => 
          prev.filter(school => school._id !== data.id)
        );
      });
    }

    return () => {
      if (socket) {
        socket.off('school_created');
        socket.off('school_updated');
        socket.off('school_deleted');
      }
    };
  }, [socket]);

  return {
    schools,
    loading,
    error,
    fetchSchools
  };
};
