import React, { useState } from 'react';
import './Reviews.css'; // We'll create this CSS file next

const Reviews = () => {
  const [reviews, setReviews] = useState([
    {
      id: 1,
      name: "Parent of Student",
      rating: 5,
      comment: "Excellent school with dedicated teachers and great facilities. My child has thrived here!",
      date: "2023-10-15"
    },
    {
      id: 2,
      name: "Alumni",
      rating: 4,
      comment: "Good overall experience. The sports program could use more funding though.",
      date: "2023-09-22"
    }
  ]);

  const [newReview, setNewReview] = useState({
    name: '',
    rating: 5,
    comment: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    const reviewToAdd = {
      ...newReview,
      id: reviews.length + 1,
      date: new Date().toISOString().split('T')[0]
    };
    setReviews([reviewToAdd, ...reviews]);
    setNewReview({ name: '', rating: 5, comment: '' });
  };

  const handleChange = (e) => {
    setNewReview({
      ...newReview,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="reviews-container">
      <h2>School Reviews</h2>
      
      {/* Review Form */}
      <form onSubmit={handleSubmit} className="review-form">
        <h3>Add Your Review</h3>
        <div className="form-group">
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={newReview.name}
            onChange={handleChange}
            required
          />
        </div>
        
        <div className="form-group">
          <label>Rating:</label>
          <select
            name="rating"
            value={newReview.rating}
            onChange={handleChange}
          >
            <option value={5}>5 Stars</option>
            <option value={4}>4 Stars</option>
            <option value={3}>3 Stars</option>
            <option value={2}>2 Stars</option>
            <option value={1}>1 Star</option>
          </select>
        </div>
        
        <div className="form-group">
          <label>Comment:</label>
          <textarea
            name="comment"
            value={newReview.comment}
            onChange={handleChange}
            required
            rows={4}
          />
        </div>
        
        <button type="submit">Submit Review</button>
      </form>

      {/* Reviews List */}
      <div className="reviews-list">
        {reviews.map(review => (
          <div key={review.id} className="review-item">
            <div className="review-header">
              <div className="reviewer-name">{review.name}</div>
              <div className="review-date">{review.date}</div>
            </div>
            <div className="review-rating">
              {'★'.repeat(review.rating)}{'☆'.repeat(5 - review.rating)}
            </div>
            <div className="review-comment">{review.comment}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Reviews;
