import { useEffect, useState, useRef } from 'react';
import io from 'socket.io-client';

export const useSocket = () => {
  const [socket, setSocket] = useState(null);
  const [notifications, setNotifications] = useState([]);
  const [isConnected, setIsConnected] = useState(false);
  const socketRef = useRef(null);

  useEffect(() => {
    // Connect to Socket.IO server
    console.log('ðŸ”Œ Connecting to WebSocket server...');
    socketRef.current = io('http://localhost:3001', {
      transports: ['websocket', 'polling']
    });

    socketRef.current.on('connect', () => {
      console.log('âœ… Connected to real-time server');
      setIsConnected(true);
      addNotification('Connected to real-time updates', 'success');
    });

    socketRef.current.on('disconnect', () => {
      console.log('âŒ Disconnected from server');
      setIsConnected(false);
    });

    socketRef.current.on('connected', (data) => {
      console.log('âœ… Server connection confirmed:', data.message);
    });

    // Connection error handling
    socketRef.current.on('connect_error', (error) => {
      console.error('Connection error:', error);
      addNotification('Connection lost - updates may not be real-time', 'warning');
    });

    socketRef.current.on('reconnect', () => {
      console.log('ðŸ”„ Reconnected to server');
      addNotification('Reconnected - real-time updates restored', 'success');
      setIsConnected(true);
    });

    setSocket(socketRef.current);

    return () => {
      if (socketRef.current) {
        console.log('ðŸ”Œ Disconnecting socket...');
        socketRef.current.disconnect();
      }
    };
  }, []);

  const addNotification = (message, type = 'info') => {
    const newNotification = {
      id: Date.now(),
      message,
      type,
      timestamp: new Date()
    };
    
    setNotifications(prev => [...prev, newNotification]);
    
    // Auto remove after 5 seconds
    setTimeout(() => {
      setNotifications(prev => prev.filter(notif => notif.id !== newNotification.id));
    }, 5000);
  };

  const removeNotification = (id) => {
    setNotifications(prev => prev.filter(notif => notif.id !== id));
  };

  return {
    socket,
    notifications,
    removeNotification,
    isConnected,
    addNotification
  };
};
