import React, { useState, useRef, useEffect } from 'react';
import './Chatbot.css'; // We'll create a separate CSS file

const Chatbot = () => {
  const [isChatActive, setIsChatActive] = useState(false);
  const [messages, setMessages] = useState([
    {
      id: Date.now(),
      text: "Hi there! I'm JBMMSI Assistant. How can I help you today?",
      sender: 'bot',
      timestamp: new Date()
    }
  ]);
  const [inputValue, setInputValue] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  // Auto-scroll to bottom when messages change
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  // Enhanced response system
  const getBotResponse = (userMessage) => {
    const lowerMessage = userMessage.toLowerCase();
    
    const responses = {
      admission: "Admission requirements include: Completed application form, Transcript of records or Form 137, and interview. Specific requirements may vary by program. Would you like more details about a specific program?",
      program: "We offer programs in Preschool (Nursery, Kindergarten), Elementary Level, One-on-One tutorial Services (Online & Face to Face), and Summer Classes. Each program has specialized tracks for advanced study.",
      fee: "Tuition fees may vary by program. On average, programs cost ₱5,000 per grading. We offer scholarships and financial aid to qualified students. Contact us for specific fee structures.",
      deadline: "The application deadline for the next academic year is June 15. Late applications may be accepted based on availability. Early application is recommended!",
      contact: "You can reach us at: 📞 Phone: +63993 617 8050 ✉️ Email: jemmonte926@gmail.com 📍 Address: Phase 6 Blk 3 Lot 4 Eastwood Residences San Isidro, Rodriguez, Philippines, 1860",
      facility: "Our campus features air-conditioned classrooms, modern teaching aids, science labs, computer labs, a library, sports facilities, and student lounges. We prioritize a conducive learning environment.",
      greeting: "Hello! Welcome to JBMMSI! How can I assist you with information about our school today?",
      default: "I'm sorry, I don't have specific information about that. For detailed inquiries, please contact our admissions office at +63993 617 8050 or email us at jemmonte926@gmail.com"
    };

    if (lowerMessage.includes('admission') || lowerMessage.includes('requirement') || lowerMessage.includes('apply') || lowerMessage.includes('enroll')) {
      return responses.admission;
    } else if (lowerMessage.includes('program') || lowerMessage.includes('course') || lowerMessage.includes('curriculum')) {
      return responses.program;
    } else if (lowerMessage.includes('fee') || lowerMessage.includes('tuition') || lowerMessage.includes('cost') || lowerMessage.includes('payment')) {
      return responses.fee;
    } else if (lowerMessage.includes('deadline') || lowerMessage.includes('application date')) {
      return responses.deadline;
    } else if (lowerMessage.includes('contact') || lowerMessage.includes('email') || lowerMessage.includes('phone') || lowerMessage.includes('address')) {
      return responses.contact;
    } else if (lowerMessage.includes('facility') || lowerMessage.includes('campus') || lowerMessage.includes('classroom')) {
      return responses.facility;
    } else if (lowerMessage.includes('hello') || lowerMessage.includes('hi') || lowerMessage.includes('hey') || lowerMessage.includes('good morning') || lowerMessage.includes('good afternoon')) {
      return responses.greeting;
    } else {
      return responses.default;
    }
  };

  const sendMessage = async () => {
    const trimmedValue = inputValue.trim();
    if (!trimmedValue) return;

    // Add user message
    const newUserMessage = {
      id: Date.now(),
      text: trimmedValue,
      sender: 'user',
      timestamp: new Date()
    };
    
    setMessages(prev => [...prev, newUserMessage]);
    setInputValue('');
    setIsTyping(true);

    // Simulate typing delay
    await new Promise(resolve => setTimeout(resolve, 1000));

    // Add bot response
    const botResponse = getBotResponse(trimmedValue);
    const newBotMessage = {
      id: Date.now() + 1,
      text: botResponse,
      sender: 'bot',
      timestamp: new Date()
    };
    
    setMessages(prev => [...prev, newBotMessage]);
    setIsTyping(false);
  };

  const handleSuggestedQuestion = (question) => {
    setInputValue(question);
    // Don't call sendMessage immediately to allow user to see the question in input
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' });
  };

  const suggestedQuestions = [
    'What are the admission requirements?',
    'What programs do you offer?',
    'How much is the tuition fee?',
    'What is your contact information?',
    'What facilities are available?',
    'When is the application deadline?'
  ];

  return (
    <div className="chatbot-container">
      {/* Chat Icon */}
      <div 
        className={`chatbot-icon ${isChatActive ? 'active' : ''}`} 
        onClick={() => setIsChatActive(!isChatActive)}
        aria-label="Open chat"
      >
        <i className="fas fa-comments"></i>
        {!isChatActive && <span className="notification-dot"></span>}
      </div>
      
      {/* Chat Box */}
      <div className={`chatbox ${isChatActive ? 'active' : ''}`}>
        <div className="chat-header">
          <div className="chat-header-info">
            <div className="avatar">J</div>
            <div>
              <h3>JBMMSI Assistant</h3>
              <span className="status">Online</span>
            </div>
          </div>
          <button 
            className="close-btn" 
            onClick={() => setIsChatActive(false)}
            aria-label="Close chat"
          >
            ×
          </button>
        </div>

        {/* Chat Messages */}
        <div className="chat-messages">
          {messages.map((message) => (
            <div 
              key={message.id} 
              className={`message ${message.sender === 'bot' ? 'bot-message' : 'user-message'}`}
            >
              <div className="message-content">
                {message.text.split('\n').map((line, i) => (
                  <div key={i}>{line}</div>
                ))}
              </div>
              <div className="message-time">
                {formatTime(message.timestamp)}
              </div>
            </div>
          ))}
          
          {isTyping && (
            <div className="message bot-message typing-indicator">
              <div className="typing-dots">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        {/* Suggested Questions */}
        {messages.length <= 2 && (
          <div className="suggested-questions">
            <p>Quick questions:</p>
            <div className="suggested-buttons">
              {suggestedQuestions.map((question, index) => (
                <button 
                  key={index}
                  className="suggested-btn" 
                  onClick={() => handleSuggestedQuestion(question)}
                >
                  {question}
                </button>
              ))}
            </div>
          </div>
        )}

        {/* Chat Input */}
        <div className="chat-input-container">
          <div className="chat-input">
            <input 
              type="text" 
              placeholder="Ask about JBMMSI..."
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onKeyPress={handleKeyPress}
              disabled={isTyping}
            />
            <button 
              onClick={sendMessage} 
              disabled={!inputValue.trim() || isTyping}
              aria-label="Send message"
            >
              <i className="fas fa-paper-plane"></i>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chatbot;
