import React, { createContext, useContext, useEffect, useState, useCallback } from 'react';
import io from 'socket.io-client';

const SocketContext = createContext();

export const useSocketContext = () => {
  const context = useContext(SocketContext);
  if (!context) {
    throw new Error('useSocketContext must be used within a SocketProvider');
  }
  return context;
};

export const SocketProvider = ({ children }) => {
  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [eventListeners, setEventListeners] = useState(new Map());

  // Function to add event listeners that will be re-established on reconnection
  const addEventListener = useCallback((event, callback) => {
    if (socket) {
      console.log(`ðŸ”Œ Adding listener for event: ${event}`);
      socket.on(event, callback);
      
      // Store the listener for reconnection
      setEventListeners(prev => {
        const newListeners = new Map(prev);
        const existingCallbacks = newListeners.get(event) || [];
        newListeners.set(event, [...existingCallbacks, callback]);
        return newListeners;
      });
    }
  }, [socket]);

  // Function to remove event listeners
  const removeEventListener = useCallback((event, callback) => {
    if (socket) {
      socket.off(event, callback);
      
      setEventListeners(prev => {
        const newListeners = new Map(prev);
        const existingCallbacks = newListeners.get(event) || [];
        const filteredCallbacks = existingCallbacks.filter(cb => cb !== callback);
        
        if (filteredCallbacks.length === 0) {
          newListeners.delete(event);
        } else {
          newListeners.set(event, filteredCallbacks);
        }
        
        return newListeners;
      });
    }
  }, [socket]);

  // Re-establish all event listeners on reconnection
  const reestablishEventListeners = useCallback(() => {
    if (socket) {
      console.log('ðŸ”Œ Re-establishing event listeners...');
      eventListeners.forEach((callbacks, event) => {
        callbacks.forEach(callback => {
          socket.on(event, callback);
        });
      });
    }
  }, [socket, eventListeners]);

  useEffect(() => {
    console.log('ðŸ”Œ Initializing WebSocket connection...');
    const socketIo = io('http://localhost:3001', {
      transports: ['websocket', 'polling'],
      timeout: 10000,
      reconnection: true,
      reconnectionAttempts: Infinity,
      reconnectionDelay: 1000,
      reconnectionDelayMax: 5000,
    });

    const handleConnect = () => {
      console.log('âœ… Connected to WebSocket server:', socketIo.id);
      setIsConnected(true);
      addNotification('Connected to real-time updates', 'success');
      
      // Re-establish all event listeners after connection
      setTimeout(() => {
        reestablishEventListeners();
      }, 100);
    };

    const handleDisconnect = (reason) => {
      console.log('âŒ Disconnected from server:', reason);
      setIsConnected(false);
      
      if (reason === 'io server disconnect') {
        // Server forced disconnect, need to manually reconnect
        socketIo.connect();
      }
    };

    const handleConnectError = (error) => {
      console.error('Connection error:', error);
      addNotification('Connection lost - updates may not be real-time', 'warning');
    };

    const handleReconnect = (attemptNumber) => {
      console.log(`ðŸ”„ Reconnected to server after ${attemptNumber} attempts`);
      addNotification('Reconnected - real-time updates restored', 'success');
      setIsConnected(true);
    };

    const handleReconnectAttempt = (attemptNumber) => {
      console.log(`ðŸ”„ Reconnection attempt ${attemptNumber}`);
    };

    const handleReconnectError = (error) => {
      console.error('Reconnection error:', error);
    };

    const handleReconnectFailed = () => {
      console.error('âŒ Failed to reconnect to server');
      addNotification('Failed to reconnect - please refresh the page', 'error');
    };

    // Set up event listeners
    socketIo.on('connect', handleConnect);
    socketIo.on('disconnect', handleDisconnect);
    socketIo.on('connect_error', handleConnectError);
    socketIo.on('reconnect', handleReconnect);
    socketIo.on('reconnect_attempt', handleReconnectAttempt);
    socketIo.on('reconnect_error', handleReconnectError);
    socketIo.on('reconnect_failed', handleReconnectFailed);

    setSocket(socketIo);

    return () => {
      console.log('ðŸ”Œ Cleaning up WebSocket connection...');
      socketIo.off('connect', handleConnect);
      socketIo.off('disconnect', handleDisconnect);
      socketIo.off('connect_error', handleConnectError);
      socketIo.off('reconnect', handleReconnect);
      socketIo.off('reconnect_attempt', handleReconnectAttempt);
      socketIo.off('reconnect_error', handleReconnectError);
      socketIo.off('reconnect_failed', handleReconnectFailed);
      socketIo.disconnect();
    };
  }, [reestablishEventListeners]);

  const addNotification = (message, type = 'info') => {
    const newNotification = {
      id: Date.now(),
      message,
      type,
      timestamp: new Date()
    };
    
    setNotifications(prev => [...prev, newNotification]);
    
    setTimeout(() => {
      setNotifications(prev => prev.filter(notif => notif.id !== newNotification.id));
    }, 5000);
  };

  const removeNotification = (id) => {
    setNotifications(prev => prev.filter(notif => notif.id !== id));
  };

  const value = {
    socket,
    isConnected,
    notifications,
    addNotification,
    removeNotification,
    addEventListener,
    removeEventListener
  };

  return (
    <SocketContext.Provider value={value}>
      {children}
    </SocketContext.Provider>
  );
};
